class TypeWriter{constructor(t,i,e=3e3){this.txtElement=t,this.words=i,this.txt="",this.wordIndex=0,this.wait=parseInt(e,10),this.type(),this.isDeleting=!1}type(){const t=this.words[this.wordIndex%this.words.length];this.txt=t.substring(0,this.isDeleting?this.txt.length-1:this.txt.length+1),this.txtElement.innerHTML=`<span class="txt">${this.txt}</span>`;let i=300;this.isDeleting&&(i/=2),this.isDeleting||this.txt!==t?this.isDeleting&&""===this.txt&&(this.isDeleting=!1,this.wordIndex++,i=500):(i=this.wait,this.isDeleting=!0),setTimeout(()=>this.type(),i)}}function init(){const t=document.querySelector(".txt-type"),i=JSON.parse(t.getAttribute("data-words")),e=t.getAttribute("data-wait");new TypeWriter(t,i,e)}document.addEventListener("DOMContentLoaded",init);